insert  into `Shop_Wares`(`ID`,`Zero_Section_ID`,`Name`,`Title`,`Keywords`,`Description`,`DescriptionSmall`,`Content`,`Imgs`) values (385,1121,'Основа для броши круглая 30мм','Основа для броши круглая 30мм','Основа для броши круглая 30мм','Металлическая заготовка для изготовления брошей с английской булавкой. Такой способ крепления броши делает изделие удобным в обращении, ее можно прикрепить с помощью булавки к одежде, к сумочке, на обувь и т.д.','Металлическая заготовка для изготовления брошей с английской булавкой. Такой способ крепления броши делает изделие удобным в обращении, ее можно прикрепить с помощью булавки к одежде, к сумочке, на обувь и т.д.','<p><strong>Основа для броши</strong> &ndash; это многофункциональная основа. <span style=\"font-family:calibri,sans-serif; font-size:11pt\">Металлическая заготовка для изготовления брошей с английской булавкой. Такой способ крепления броши делает изделие удобным в обращении, ее можно прикрепить с помощью булавки к одежде, к сумочке, на обувь и т.д.&nbsp;</span>Декоративные элементы крепятся с помощью клея пистолета, пришивания через специальные отверстия. В декорировании броши используют бусины, стразы, бисер, ленты, искусственные цветы и разнообразные декоративные элементы.</p>','shop_wares/100/100/100/100/100/100/100/100/100/103/385/Osnova_dlya_broshi_kruglaya_30mm_nikel.JPG');
