insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1090,1050,'www/product/bizhuteriya','bizhuteriya',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',1,'Бижутерия','Бижутерия','Бижутерия','Бижутерия','<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; Бижутерия это стильные и модные женские украшения &nbsp;из не драгоценных металлов; натуральных и искусственных камней; стекла и т.п. Расширенный ассортимент бижутерии позволяет подобрать как стильные и модные аксессуары так и наборы из коллекций имитирующих &nbsp;известные марки.</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1091,1050,'www/product/broshi_i_bulavki','broshi_i_bulavki',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',2,'Броши и булавки','Броши и булавки','Броши и булавки','Броши и булавки','<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; Все броши в нашем магазине выполены из качественных материалов и прослужат не один год. Такое украшение поможет подчеркнуть особенность вашей внешности или наряда. Стройным женщинам подойдут любые броши, а вот дамам с крупными формами лучше выбрать округлую брошь с плавными линиями.</span></span></p>\r\n\r\n<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; А вы знали, что форма броши несет определенную информацию о своей обладательнице? Так, если вы романтичная модница, то ваш выбор винтаж. Брошь с нежным цветочным орнаментом расскажет о мягком характере женщины, а прямые линии и острые углы - знак целенаправленного характера.</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1092,1050,'www/product/ukrasheniya_dlya_volos','ukrasheniya_dlya_volos',NULL,'Www_Main','Shop_Wares_Page','no','no','no','yes',3,'Украшения для волос','Украшения для волос','Украшения для волос','Украшения для волос','<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; В нашем магазине вас ждет богатый выбор украшений для волос. Здесь вы обязательно найдете бижутерию на ваш вкус: от ярких молодежных резинок и заколок на каждый день до шикарных подвязок с сияющими кристаллами.</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1093,1050,'www/product/businy_podveski','businy_podveski',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',4,'Бусины, подвески','Бусины, подвески','Бусины, подвески','Бусины, подвески','<p>&nbsp; <span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">Создавайте украшения сами! Все что вам нужно - это <a href=\"http://osnovy_dlya_brasletov\">основы для браслетов</a> или <a href=\"http://Busy\">бус</a> подходящей длины, подвески из нашего магазина на любой вкус и ваша фантазия.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; У нас вы найдете бусины и подвески из дерева, металла, качественного пластика, стекла, или камня. Сочетайте их как вашей душе угодно, и такое украшение станет единственным и неповторимым. Вы всегда сможете изменить или полностью переделать ваш браслет под новый наряд или образ. Также вас ждет большой выбор другой <a href=\"http://furnitura_dlya_bizhuterii\">фурнитуры для бижутерии</a>.&nbsp; </span></span>&nbsp;</p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1094,1050,'www/product/furnitura_dlya_bizhuterii','furnitura_dlya_bizhuterii',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',6,'Фурнитура для бижутерии','Фурнитура для бижутерии','Фурнитура для бижутерии','Фурнитура для бижутерии','<p>&nbsp;<span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\"> Теперь почти любое украшение вы можете создать своими руками! Используйте нашу качественную и надежную фурнитуру для бижутерии. В вашем распоряжении основы под браслеты и бусы разной длины, а также огромный выбор бусин и подвесок.</span></span></p>\r\n\r\n<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; В нашем магазине вы найдете застежки-карабины самой разной формы, зажимные бусины, держатели для сережек, соединительные кольца, шнуры и массу другой фурнитуры для бижутерии. Вся она сделана из качественных материалов и прослужит не год и не два.</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1095,1050,'www/product/instrumenty','instrumenty',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',7,'Инструменты','Инструменты','Инструменты','Для создания бижутерии понадобятся инструменты, которые значительно облегчает  процесс  творчества. В этом разделе сможете подобрать любой инструмент необходимые для Вашего творчества . В раздел инструменты входят ювелирные круглогубцы, плоскогубцы и бокорезы (кусачки) и т.п.','<p>&nbsp; &nbsp;Большая часть инструментов не является обязательной, но все же значительно облегчает процесс работы и творчества. В этом разделе сможете подобрать любой инструмент необходимый для работы, это ювелирные круглогубцы, плоскогубцы и бокорезы (кусачки) и т.п</p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1096,1090,'www/product/bizhuteriya/busy','busy',NULL,'Www_Main','Shop_Wares_Page','no','yes','no','yes',0,'Бусы','Бусы','Бусы','Бусы','<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; В нашем магазине вы найдете качественные и недорогие бусы из ювелирных сплавов, пластика и натуральных материалов на любой вкус. Благодаря широкому выбору вы сможете подобрать уникальный и запоминающийся подарок или порадовать себя.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; Выбирая бусы, ориентируйтесь в первую очередь на длину украшения, а не размер бусин. Длинные бусы зрительно вытягивают фигуру и хорошо смотрятся на невысоких женщинах. Стройным девушкам подойдут бусы с мелкимии элементами или крупными, но нанизанными не подряд, а дамам в теле - с большими бусинами или мелкими в несколько витков.</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1097,1090,'www/product/bizhuteriya/kole_podveski','kole_podveski',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',0,'Колье, подвески','Колье, подвески','Колье, подвески','Колье, подвески.','<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; Колье и подвески, представленные в нашем магазине, выполнены из качественных материалов, благодаря чему эти украшения прослужат очень долго. Наша продукция ничем не уступает настоящим драгоценностям и станет хорошим подарком на любое торжество.</span></span></p>\r\n\r\n<p><span style=\"font-family:georgia,serif\"><span style=\"font-size:14px\">&nbsp; Прежде чем купить колье или подвеску, подумайте, с каким платьем они будут носиться. Если ваш выбор - открытые плечи и широкий вырез, то сюда подойдет короткое ожерелье, обхватывающее шею. С глубоким декольте также хорошо смотрится кулон на тонкой цепочке. Для любительниц делового стиля лучший вариант - тонкая цепочка или серебряное ожерелье.&nbsp; &nbsp;</span></span></p>');
insert  into `Zero_Section`(`ID`,`Zero_Section_ID`,`Url`,`UrlThis`,`UrlRedirect`,`Layout`,`Controller`,`IsAuthorized`,`IsEnable`,`IsVisible`,`IsIndex`,`Sort`,`Name`,`Title`,`Keywords`,`Description`,`Content`) values (1098,1090,'www/product/bizhuteriya/braslety','braslety',NULL,'Www_Main','Shop_Wares_Page','no','yes','yes','yes',0,'Браслеты','Браслеты','Браслеты','Браслеты','<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; Браслеты, украшенные стразами, кристаллами, хрустальными или янтарными бусинами, помогут вам создать свой неповторимый образ. Все украшения выполнены из качественных материалов и будут радовать свою обладательницу много-много лет.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; Выбирая браслет, ориентируйтесь на его визуальный &quot;вес&quot;, а не реальную ширину. Для худых запястий подойдут узкие браслеты или широкие, но с воздушным ажурным дизайном. Также хорошо будут смотреться браслеты пестрые и украшенные подвесками.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:georgia,serif\">&nbsp; Дамам с широкими запястьями стоит обратить внимание на браслеты с крупными бусинами и сложной формы. Можно даже использовать сразу несколько тонких и широких браслетов в самых разных сочетаниях.</span></span></p>');
